mapper.select:
SELECT
	ai.id,ai.type_id,ai.title, ai.content, ai.content_text,ai.cover,ai.view_count,ai.status,ai.update_time,ti.name
FROM
	article_info ai,type_info ti
where 
	ai.type_id = ti.id and ai.user_id = #{user_id}
	 AND ai.type_id = #{param.typeId} 
	 AND ai.update_time BETWEEN  #{param.startDate} AND #{param.endDate} 
	 AND ai.title LIKE #{param.keyWord}% 
	 AND ai.status = #{param.status} 
ORDER BY ai.update_time DESC

mapper.update:
UPDATE article_info
type_id=#{typeId}, title=#{title}, status=#{status}, user_id=#{userId}, view_count=#{viewCount}, content=#{content}, content_text=#{contentText}, cover=#{cover}, update_time=#{updateTime},
WHERE id = #{id}

UPDATE article_info
	set view_count= #{viewCount}
where 
  	id = #{id}
